const Users = require("expense-libs/models/User");

class UserServices {

    createUser = async (data) => {
        try {
            const newUser = new Users(data)
            const result = await newUser.save()
            if (result) {
                return result
            }
            return false
        } catch (error) {
            console.log("error", error)
            return false
        }
    }

}

module.exports = UserServices;